const BASE_IMAGE_URL = 'https://static.wixstatic.com/media/';

function parseCSV(text: string): string[][] {
  const rows: string[][] = [];
  let currentRow: string[] = [];
  let currentField = '';
  let inQuote = false;

  for (let i = 0; i < text.length; i++) {
    const char = text[i];
    const nextChar = text[i + 1];

    if (char === '"') {
      if (inQuote && nextChar === '"') {
        currentField += '"';
        i++;
      } else {
        inQuote = !inQuote;
      }
    } else if (char === ',' && !inQuote) {
      currentRow.push(currentField);
      currentField = '';
    } else if ((char === '\n' || char === '\r') && !inQuote) {
      if (char === '\r' && nextChar === '\n') i++;
      currentRow.push(currentField);
      if (currentRow.length > 0) rows.push(currentRow);
      currentRow = [];
      currentField = '';
    } else {
      currentField += char;
    }
  }
  if (currentField || currentRow.length > 0) {
    currentRow.push(currentField);
    rows.push(currentRow);
  }
  return rows;
}

export const getProducts = (): Product[] => {
  const rows = parseCSV(RAW_CSV);
  const products: Product[] = [];
  
  // Skip header row
  for (let i = 1; i < rows.length; i++) {
    const row = rows[i];
    if (row.length < 2) continue;
    
    // Only process 'Product' rows
    if (row[1] !== 'Product') continue;

    const rawImages = row[4] ? row[4].split(';') : [];
    const images = rawImages.map(img => 
      img.startsWith('http') ? img : `${BASE_IMAGE_URL}${img}`
    );

    const price = parseFloat(row[8]);
    
    const product: Product = {
      id: row[0],
      name: row[2],
      description: row[3],
      price: isNaN(price) ? 0 : price,
      images,
      category: row[5],
      visible: row[10] === 'true',
      inventory: row[13],
      ribbon: row[7]
    };

    if (product.visible) {
      products.push(product);
    }
  }
  return products;
};

export const getCategories = (products: Product[]): string[] => {
  const categories = new Set<string>();
  products.forEach(p => {
    if (p.category) {
      const cats = p.category.split(';');
      cats.forEach(c => {
        const trimmed = c.trim();
        if (trimmed) categories.add(trimmed);
      });
    }
  });
  return ['All', ...Array.from(categories)];
};
